const request     = require('supertest');
const expect      = require('chai').expect;
const server      = require('./lib/express');
const registrationData = require('./lib/registrationData');

describe('Get Workers', function () {

  it.only('base', function(done) {
    request(server)
      .post('/')
      .send({
        event: 'MODULE_EXEC',
        payload: {
          moduleId: 'get_workers',
          moduleParam: {
          },
          registrationData,
          invokerOAuth: {
            access_token: 123
          },
          providerOAuth: {
            access_token: 123
          }
        }
      })
      .set('X_CONVERSE_APP_TOKEN', require('../app-token'))
      .expect(200)
      .end(function(err, res) {
        expect(res.body).to.have.property('status').to.equal(0);
        expect(res.body).to.have.property('value');
        done();
      });
  })

});
